import React, { Component } from 'react';
import GG from '../Images/golden_gate.jpg';
import PointLobos from '../Images/point_lobos.jpg';
import RCMH from '../Images/rcmh.jpg';
import IV from '../Images/iv.jpg';
import LoneTree from '../Images/lone_tree.jpg';
import NYC from '../Images/new_york.jpg';
import Laguna from '../Images/laguna.jpg';
import GC from '../Images/grand_canyon.jpg';
import SD from '../Images/san_diego.jpg';

export class Images extends Component {
  enlarge(imgSrc) {
    var lbContent = document.createElement('img');
    lbContent.className = 'lightbox-content';
    lbContent.src = imgSrc;
    lbContent.id = 'lbContent';
    var lightbox = document.createElement('div');
    lightbox.id = 'lb';
    lightbox.className = 'lightbox';
    document.body.appendChild(lightbox);  
    document.getElementById('lb').appendChild(lbContent);
    document.getElementById('lb').addEventListener('click', function(event) {
      if(event.target.className !== 'lightbox-content') {
        document.getElementById('lb').removeChild(document.getElementById('lbContent'));
        document.body.removeChild(document.getElementById('lb'));
      }
    });
  }

  /*enlarge(imgSrc, id) {
    var enlargedImg = document.getElementById("big"+id);
    var lightbox = document.getElementById("box"+id);
    lightbox.style.display = "flex";
    enlargedImg.src = imgSrc;

    window.onclick = function(event) {
        if (event.target.className === "lightbox") {
            lightbox.style.display = "none";
        }
    }
  }*/

  render() {
    const images = [
      {
        id: 1,
        src: GG
      },
      {
        id: 2,
        src: PointLobos
      },
      {
        id: 3,
        src: RCMH
      },
      {
        id: 4,
        src: IV
      },
      {
        id: 5,
        src: LoneTree
      },
      {
        id: 6,
        src: NYC
      },
      {
        id: 7,
        src: Laguna
      },
      {
        id: 8,
        src: GC
      },
      {
        id: 9,
        src: SD
      },
    ]
    return (
      <div>
        <div className='content'>
          <div className='img-container'>
            <div className='img-child'>
              <img src={GG} id='01' onClick={this.enlarge.bind(this, GG, '01')} alt='Golden Gate Bridge'/>
              <div className='caption'>
                I took this photo when my family and I visited 
                San Francisco over winter break this year.
              </div>
            </div>
            <div id='box01' className='lightbox'>
              <img className='lightbox-content' id='big01' alt='Enlarged Golden Gate Bridge'/>
            </div>
            <div className='img-child'>
              <img src={PointLobos} id='02' onClick={this.enlarge.bind(this, PointLobos, '02')} alt='Point Lobos'/>
              <div className='caption'>
                When my family took a trip along California's
                coast, I took this photo at Point Lobos.
              </div>
            </div>
            <div id='box02' className='lightbox'>
              <img className='lightbox-content' id='big02' alt='Enlarged Point Lobos'/>
            </div>
            <div className='img-child'>
              <img src={RCMH} id='03' onClick={this.enlarge.bind(this, RCMH, '03')} alt='Radio City Music Hall'/>
              <div className='caption'>
                I took this photo back in 2017 when I visited
                New York before college.
              </div>
            </div>
            <div id='box03' className='lightbox'>
              <img className='lightbox-content' id='big03' alt='Enlarged Radio City Music Hall'/>
            </div>
            <div className='img-child'>
              <img src={IV} id='04' onClick={this.enlarge.bind(this, IV, '04')} alt='Isla Vista Sunset'/>
              <div className='caption'>
                During Welcome Week this past year, I watched
                the sunrise with my friend and took this photo.
              </div>
            </div>
            <div id='box04' className='lightbox'>
              <img className='lightbox-content' id='big04' alt='Enlarged Isla Vista Sunset'/>
            </div>
            <div className='img-child'>
              <img src={LoneTree} id='05' onClick={this.enlarge.bind(this, LoneTree, '05')} alt='Lone Tree Point'/>
              <div className='caption'>
                This photo was taken when my family and I drove
                through Pebble Beach's scenic 17-mile drive.
              </div>
            </div>
            <div id='box05' className='lightbox'>
              <img className='lightbox-content' id='big05' alt='Enlarged Lone Tree Point'/>
            </div>
            <div className='img-child'>
              <img src={NYC} id='06' onClick={this.enlarge.bind(this, NYC, '06')} alt='New York Buildings'/>
              <div className='caption'>
                I took this photo from the Top of the Rock in 
                New York. The view was pretty amazing!
              </div>
            </div>
            <div id='box06' className='lightbox'>
              <img className='lightbox-content' id='big06' alt='Enlarged New York Buildings'/>
            </div>
            <div className='img-child'>
              <img src={Laguna} id='07' onClick={this.enlarge.bind(this, Laguna, '07')} alt='Tablerock Beach'/>
              <div className='caption'>
                When my friends and I visited Tablerock Beach
                in Laguna, I captured this photo.
              </div>
            </div>
            <div id='box07' className='lightbox'>
              <img className='lightbox-content' id='big07' alt='Enlarged Tablerock Beach'/>
            </div>
            <div className='img-child'>
              <img src={GC} id='08' onClick={this.enlarge.bind(this, GC, '08')} alt='Grand Canyon'/>
              <div className='caption'>
                During last year's winter break, I took this
                picture while on vacation with my family.
              </div>
            </div>
            <div id='box08' className='lightbox'>
              <img className='lightbox-content' id='big08' alt='Enlarged Grand Canyon'/>
            </div>
            <div className='img-child'>
              <img src={SD} id='09' onClick={this.enlarge.bind(this, SD, '09')} alt='San Diego'/>
              <div className='caption'>
                I visited my friends in San Diego during last 
                year's spring break (what a different time it 
                was!) and took this picture.
              </div>
            </div>
            <div id='box09' className='lightbox'>
              <img className='lightbox-content' id='big09' alt='Enlarged San Diego'/>
            </div>
          </div>
        </div>
        <button className='scrollButton' style={{display: this.props.display}} onClick={this.props.scrollToTop}>Scroll to Top</button>
      </div>
    );
  }
}
export default Images;
