import React, { Component } from 'react';

export class Tab extends Component {
  addStyling = () => {
    if(this.props.tab.id === this.props.activeTab) {
      return {
        backgroundColor: '#507A77',
        color: 'white'
      }
    } else if (this.props.tab.id === this.props.hoverTab) {
      return {
        backgroundColor: '#8FBDB9',
        borderBottom: '2px solid #507A77'
      }
    } else {
      return {
        backgroundColor: '#A9DBD7',
        borderBottom: '2px solid transparent',
        color: '#111'
      }
    }
  }
  addHoverStyling = () => {

  }
  render() {
    return(
      <div className='tab' style={this.addStyling()} onMouseOver={this.props.hoverTab.bind(this, this.props.tab.id)} onClick={this.props.changeTab.bind(this, this.props.tab.id)}>
        <p>{this.props.tab.title}</p>
      </div>
    );
  }
}
export default Tab;
